import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./Expenses.css";
import ExpensesFilter from "./ExpenseFilter";
import {useState} from "react";
let selected;

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("2020");
  const filteredArr = [];

  const onSelectYear = (selected) => {
    setFilteredYear(selected);
    props.items.filter((expense) => {
      let expenseYear = expense.date.toString().split(" ")[3];
      // console.log(expenseYear);
      console.log(filteredYear);
      if (filteredYear === expenseYear) {
        filteredArr.push(expense);
        console.log(filteredArr);
      }
      return filteredArr;
    });
    console.log(selected ? true : false);
    return selected;
  };

  console.log(selected);
  return (
    <Card className="expenses">
      <ExpensesFilter
        initialFilterSetting={filteredYear}
        onSelectYear={onSelectYear}
      />
      {selected ? true : false}
      {props.items.map((expense) => {
        return (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        );
      })}
    </Card>
  );
};

export default Expenses;
