import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./Expenses.css";
import ExpensesFilter from "./ExpenseFilter";
import {useState} from "react";
const filteredArr = [];
let expenseYear = '';

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("2020");

  const onSelectYear = (selected) => {
    setFilteredYear(selected);
    props.items.filter((expense) => {
      expenseYear = expense.date.toString().split(" ")[3];
      if (filteredYear === expenseYear) {
        filteredArr.push(expense);
        console.log(filteredArr.length);
      }
      return filteredArr;
    });
  };

  console.log(expenseYear ? true : false)

  return (
    <Card className="expenses">
      <ExpensesFilter
        initialFilterSetting={filteredYear}
        onSelectYear={onSelectYear}
      />

      {filteredArr.length === 0
        ? props.items.map((expense) => {
            return (
              <ExpenseItem
                key={expense.id}
                title={expense.title}
                amount={expense.amount}
                date={expense.date}
              />
            );
          })
        : filteredArr.map((expense) => {
            return (
              <ExpenseItem
                key={expense.id}
                title={expense.title}
                amount={expense.amount}
                date={expense.date}
              />
            );
          })}
    </Card>
  );
};

export default Expenses;
